import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import MovieDetail from "../components/MovieDetail";

function Detail() {
  const [loading, setLoading] = useState(true);
  const [detail, setDetail] = useState([]);
  const { id } = useParams();

  //https://velog.io/@rgfdds98/debuging-React-Hook-useEffect-has-a-missing-dependency-fetchMovieData.-Either-include-it-or-remove-the-dependency-array
  //useEffect 사용 시 React Hook 관련 경고가 떴을 때 해결하는 방법
  useEffect(() => {
    const getDetail = async () => {
      const json = await (
        await fetch(`https://yts.mx/api/v2/movie_details.json?movie_id=${id}`)
      ).json();
      setDetail(json.data.movie);
      setLoading(false);

      console.log(typeof json.data.movie);
    };

    getDetail();
  }, [id]);

  return (
    <div>
      {loading ? (
        <h1>Loading Details...</h1>
      ) : (
        <div>
          {Object.keys(detail).map((detail) => (
            <MovieDetail
              key={detail.id}
              coverImg={detail.medium_cover_image}
              title={detail.title}
              summary={detail.summary}
            />
          ))}
          {/* {Array.isArray(detail)
            ? Object.keys(detail).map((detail) => {
                <div key={detail.id}>
                  <h2>{detail.title}</h2>;
                </div>;
              })
            : console.log(typeof detail)} */}
        </div>
      )}
    </div>
  );
}

export default Detail;
